mapper.insert:
INSERT INTO stu (
    id, name, no, gender, phone, degree, id_card, is_college, address,
    graduation_date, violation_count, violation_score, clazz_id, create_time, update_time, clazz_name
)
VALUES (
           #{id}, #{name}, #{no}, #{gender}, #{phone}, #{degree}, #{idCard}, #{isCollege}, #{address},
           #{graduationDate}, #{violationCount}, #{violationScore}, #{clazzId}, #{createTime}, #{updateTime}, #{clazzName}
       )

mapper.update:
UPDATE stu
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, degree = #{degree}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, graduation_date = #{graduationDate}, violation_count = #{violationCount}, violation_score = #{violationScore}, clazz_id = #{clazzId}, create_time = #{createTime}, update_time = #{updateTime}, clazz_name = #{clazzName},
WHERE id = #{id}

UPDATE stu
SET violation_count = violation_count + 1,
    violation_score = violation_score + #{score}
WHERE id = #{id}

mapper.select:
select
    s.*,
    c.name as clazzName
from stu s
left join clazz c on s.clazz_id = c.id


        s.name like concat('%',#{name},'%')


        and s.no like concat('%',#{no},'%')


        and s.gender = #{gender}


        and s.phone = #{phone}


        and s.degree = #{degree}


        AND s.violation_count = #{violationCount}


        AND s.violation_score = #{violationScore}


        AND s.clazz_id = #{clazzId}


        AND c.name LIKE CONCAT('%', #{clazzName}, '%')


        AND s.update_time >= #{updateTime}


order by s.update_time desc
